#offcanvas--wrapper {
  position: relative;
  overflow: hidden;
  width: 100%;
}

.offcanvas--canvas {
  width: 100%;
  height: 100%;
  position: relative;
  -webkit-transform: translateX(0);
  transform: translateX(0);
  -webkit-transform: translate3d(0, 0, 0);
  transform: translate3d(0, 0, 0);
  -webkit-transition: 300ms ease all;
  transition: 300ms ease all;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  opacity: 1;
}

.offcanvas--content {
  min-height: 100vh;
}

.offcanvas--show--right .offcanvas--canvas {
  -webkit-transform: translateX(-600px);
  transform: translateX(-600px);
  -webkit-transform: translate3d(-600px, 0, 0);
  transform: translate3d(-600px, 0, 0);
}

.offcanvas--show--left .offcanvas--canvas {
  -webkit-transform: translateX(600px);
  transform: translateX(600px);
  -webkit-transform: translate3d(600px, 0, 0);
  transform: translate3d(600px, 0, 0);
}

.offcanvas--show--right .offcanvas--content {
  opacity: 0.25;
}

.offcanvas--show--left .offcanvas--content {
  opacity: 0.25;
}

.offcanvas--canvas .offcanvas--left--sidebar {
  width: 600px;
  position: absolute;
  top: 0;
  left: -600px;
}

.offcanvas--canvas .offcanvas--right--sidebar {
  width: 600px;
  position: absolute;
  top: 0;
  right: -600px;
}

body {
  padding: 0;
  margin: 0;
}
